page_settings.custom_css:
.gc-walkthrough .swiper-slide.swiper-slide-next {
    width: 45% !important;
}

.gc-walkthrough .swiper-slide-active[data-swiper-slide-index="0"] {
    margin-left: -10% !important;
}


.gc-walkthrough .swiper-slide-active {
    margin-left: -22%;
    width: 40% !important;
}


.swiper-slide.swiper-slide-duplicate-prev {
    width: 40% !important;
}



.gc-home-gallery .elementor-image-carousel-wrapper:before {
    position: absolute;
    background: #fff;
    content: '';
    width: 275px;
    bottom: 0;
    left: 0;
    z-index: 9;
    height: 80px;
}

.gc-home-gallery .elementor-image-carousel-wrapper:after {
    position: absolute;
    background: #fff;
    content: '';
    width: 262px;
    top: 0;
    right: 0;
    z-index: 9;
    height: 80px;
}

.gc-home-gallery {
    position: relative !important;
}

.gc-home-gallery .elementor-swiper-button-prev, .gc-home-gallery  .elementor-swiper-button-next {
    top: unset;
    color: #000;
    bottom: 2.5%;
    left: 33%;
    z-index: 999;
    transform: translate(15%, -50%);
}
